Get-WmiObject -ClassName win32_ComputerSystem -ErrorAction SilentlyContinue | ft @{Name="Hostname";Expression={$_.Name}}; Get-WmiObject -Class Win32_Computersystem | ft @{Name="Usuario Logado";Expression={$_.Username}}; Get-WmiObject -Class Win32_Baseboard | ft @{Name="PLACA MAE";Expression={$_.Product}}; Get-WmiObject -Class Win32_Processor | ft @{Name="Processador";Expression={$_.Name}}; Get-WmiObject -Class Win32_OperatingSystem | ft @{Name="Arquitetura OS";Expression={$_.OSArchitecture}}; Get-WmiObject -Class Win32_VideoController | ft @{Name="Placa de Video";Expression={$_.Name}}; Get-WmiObject -Class Win32_PhysicalMemory | ft @{Name="RAM Slot";Expression={$_.BankLabel}},@{Name="Tamanho (GB)";Expression={[math]::round($_.Capacity/1GB,0)}}; Get-WmiObject -Class Win32_LogicalDisk | ? DeviceID -Like *C* | ft @{Name="Disco";Expression={$_.Name}},@{Name="Livre (GB)";Expression={[math]::round($_.FreeSpace/1GB, 0)}},@{Name="Tamanho (GB)";Expression={[math]::round($_.Size/1GB, 0)}}; pause
